mapper.select:
SELECT COUNT(*) 
  FROM admin_tb 
 WHERE ad_id = #{ad_id}

SELECT COUNT(*)
  FROM admin_tb
 WHERE ad_email = #{value}

SELECT COUNT(*)
  FROM admin_tb
 WHERE ad_empnum = #{value}

SELECT *
  FROM admin_tb
 WHERE ad_id = #{ad_id}
   AND ad_pwd= #{ad_pwd}

mapper.update:
UPDATE admin_tb
   SET ad_pwd= #{ad_pwd}, ad_name= #{ad_name}, ad_birth= #{ad_birth}, ad_phone= #{ad_phone}, ad_email= #{ad_email}, ad_zip= #{ad_zip}, ad_tel= #{ad_tel}, ad_empnum= #{ad_empnum}, ad_dep= #{ad_dep}, ad_terms= #{ad_terms}
 WHERE ad_id = #{ad_id}

UPDATE admin_tb
   SET delete_status = 'Y'
 WHERE ad_id = #{ad_id}
   AND ad_pwd= #{ad_pwd}
